                              },
                              "operator": {
                                "type": "string",
                                "description": "Determine how to evaluate criteria against the field\u2014`exists` returns results if a person in the audience has the attribute; `eq` returns results if the audience has the attribute and the attribute has the `value` you specify.",
                                "enum": [
                                  "eq",
                                  "exists"
                                ]
                              },
                              "value": {
                                "type": "string",
                                "description": "The value you want to match for this attribute. You must include a value if you use the `eq` operator."
                              }
                            },
                            "example": {
                              "field": "unsubscribed",
                              "operator": "eq",
                              "value": true
                            }
