http_endpoint.request_body_spec:
{
  "required": true,
  "description": "Returns all AR codes for a specified management company.",
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "auth",
          "method"
        ],
        "type": "object",
        "properties": {
          "auth": {
            "required": [
              "type"
            ],
            "type": "object",
            "properties": {
              "type": {
                "type": "string",
                "enum": [
                  "apikey"
                ]
              }
            }
          },
          "requestId": {
            "type": "string",
            "description": "an arbitary value to relate with response."
          },
          "method": {
            "required": [
              "name"
            ],
            "type": "object",
            "properties": {
              "name": {
                "type": "string",
                "enum": [
                  "getArCodes"
                ]
              },
              "version": {
                "type": "string",
                "enum": [
                  "r1"
                ]
              },
              "params": {
                "type": "object",
                "properties": {
                  "propertyId": {
                    "type": "integer",
                    "format": "int64",
                    "example": 123456,
                    "description": "This is an optional field. This field accepts single value. Provide a valid propertyid."
                  }
                }
              }
            }
          }
        }
      },
      "examples": {
        "getArCodes": {
          "$ref": "#/components/examples/getArCodesRequest"
        }
      }
    }
  }
}
