http_endpoint.request_body_spec:
{
  "required": true,
  "description": "Retrieves AR payments details with reference to a Payment ID and for a selected date range.",
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "auth",
          "method"
        ],
        "type": "object",
        "properties": {
          "auth": {
            "required": [
              "type"
            ],
            "type": "object",
            "properties": {
              "type": {
                "type": "string",
                "enum": [
                  "apikey"
                ]
              }
            }
          },
          "requestId": {
            "type": "string",
            "description": "an arbitary value to relate with response."
          },
          "method": {
            "required": [
              "name"
            ],
            "type": "object",
            "properties": {
              "name": {
                "type": "string",
                "enum": [
                  "getArPayments"
                ]
              },
              "version": {
                "type": "string",
                "enum": [
                  "r1"
                ]
              },
              "params": {
                "type": "object",
                "properties": {
                  "arPaymentIds": {
                    "type": "string",
                    "format": "string",
                    "example": 123456,
                    "description": "This is an optional field. This field accepts comma seperated multiple values. Comma separated ar payment ids."
                  },
                  "paymentStatusTypeIds": {
                    "type": "string",
                    "format": "string",
                    "example": 123456,
                    "description": "This is an optional field. This field accepts comma seperated multiple values. Comma separated payment status type ids."
                  },
                  "fromDate": {
                    "type": "string",
                    "format": "date",
                    "example": "MM/DD/YYYY",
                    "description": "This is an optional field. This field accepts single value. From date."
                  },
                  "toDate": {
                    "type": "string",
                    "format": "date",
                    "example": "MM/DD/YYYY",
                    "description": "This is an optional field. This field accepts single value. To date"
                  },
                  "isIncludeAllocations": {
                    "type": "integer",
                    "format": "integer",
                    "enum": [
                      0,
                      1
                    ],
                    "description": "This is an optional field. This field accepts single value. This node should include allocations details of payment"
                  }
                },
                "required": [
                  "applicationId",
                  "propertyId"
                ]
              }
            }
          }
        }
      },
      "examples": {
        "getArPayments": {
          "$ref": "#/components/examples/getArPaymentsRequest"
        }
      }
    }
  }
}
