http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "allOf": [
          {
            "type": "object",
            "properties": {
              "language": {
                "description": "The language for this language skill.",
                "allOf": [
                  {
                    "required": [
                      "id"
                    ],
                    "type": "object",
                    "properties": {
                      "id": {
                        "pattern": "^(?:(?:[0-9a-f]{32})|(?:[0-9]+\\$[0-9]+)|(\\S+=\\S+))$",
                        "type": "string",
                        "description": "wid / id / reference id"
                      },
                      "descriptor": {
                        "type": "string",
                        "description": "The display name of the instance",
                        "readOnly": true
                      },
                      "href": {
                        "type": "string",
                        "description": "A link to the instance",
                        "readOnly": true
                      }
                    }
                  },
                  {
                    "type": "object"
                  }
                ]
              },
              "abilities": {
                "type": "array",
                "description": "The abilities associated with this language skill.",
                "items": {
                  "allOf": [
                    {
                      "type": "object",
                      "properties": {
                        "proficiency": {
                          "description": "The proficiency for a specific language ability.",
                          "allOf": [
                            {
                              "required": [
                                "id"
                              ],
                              "type": "object",
                              "properties": {
                                "id": {
                                  "pattern": "^(?:(?:[0-9a-f]{32})|(?:[0-9]+\\$[0-9]+)|(\\S+=\\S+))$",
                                  "type": "string",
                                  "description": "wid / id / reference id"
                                },
                                "descriptor": {
                                  "type": "string",
                                  "description": "The display name of the instance",
                                  "readOnly": true
                                },
                                "href": {
                                  "type": "string",
                                  "description": "A link to the instance",
                                  "readOnly": true
                                }
                              }
                            },
                            {
                              "type": "object"
                            }
                          ]
                        },
                        "abilityType": {
                          "description": "The type of language ability. See the configured types in the Maintain Language Ability Types.",
                          "allOf": [
                            {
                              "required": [
                                "id"
                              ],
                              "type": "object",
                              "properties": {
                                "id": {
                                  "pattern": "^(?:(?:[0-9a-f]{32})|(?:[0-9]+\\$[0-9]+)|(\\S+=\\S+))$",
                                  "type": "string",
                                  "description": "wid / id / reference id"
                                },
                                "descriptor": {
                                  "type": "string",
                                  "description": "The display name of the instance",
                                  "readOnly": true
                                },
                                "href": {
                                  "type": "string",
                                  "description": "A link to the instance",
                                  "readOnly": true
                                }
                              }
                            },
                            {
                              "type": "object"
                            }
                          ]
                        },
                        "id": {
                          "type": "string",
                          "description": "Id of the instance"
                        }
                      }
                    }
                  ]
                },
                "x-workday-type": "Multi-instance"
              },
              "native": {
                "type": "boolean",
                "description": "If true, this language skill is the native language.",
                "example": true,
                "x-workday-type": "Boolean"
              },
              "id": {
                "type": "string",
                "description": "Id of the instance"
              }
            }
          }
        ]
      }
    }
  },
  "required": true
}
