http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "allOf": [
          {
            "required": [
              "defaultScorecardGoals",
              "effectiveDate",
              "scorecardName"
            ],
            "type": "object",
            "properties": {
              "defaultScorecardGoals": {
                "type": "array",
                "description": "The \\~goals\\~ for the Compensation Scorecard.",
                "items": {
                  "allOf": [
                    {
                      "required": [
                        "goalName",
                        "goalWeight"
                      ],
                      "type": "object",
                      "properties": {
                        "goalWeight": {
                          "type": "integer",
                          "description": "The weight given to the Compensation Scorecard Goal. [90% will be displayed as 0.9]",
                          "x-workday-type": "Numeric"
                        },
                        "goalName": {
                          "type": "string",
                          "description": "The name of the Compensation Scorecard Goal.",
                          "example": "Lorem ipsum dolor sit amet, cum choro singulis consectetuer ut, ubique iisque contentiones ex duo. Quo lorem etiam eu.",
                          "x-workday-type": "Text"
                        },
                        "goalDescription": {
                          "type": "string",
                          "description": "The description of the Compensation Scorecard Goal",
                          "example": "Lorem ipsum dolor sit amet, cum choro singulis consectetuer ut, ubique iisque contentiones ex duo. Quo lorem etiam eu.",
                          "x-workday-type": "Text"
                        },
                        "id": {
                          "type": "string",
                          "description": "Id of the instance"
                        }
                      }
                    }
                  ]
                },
                "x-workday-type": "Multi-instance"
              },
              "scorecardName": {
                "type": "string",
                "description": "The name of the Compensation Scorecard.",
                "example": "Lorem ipsum dolor sit amet, cum choro singulis consectetuer ut, ubique iisque contentiones ex duo. Quo lorem etiam eu.",
                "x-workday-type": "Text"
              },
              "inactive": {
                "type": "boolean",
                "description": "Indicates whether the Scorecard is inactive using a value of true/false.",
                "example": true,
                "x-workday-type": "Boolean"
              },
              "scorecardProfiles": {
                "type": "array",
                "description": "The profiles for the Compensation Scorecard.",
                "items": {
                  "allOf": [
                    {
                      "required": [
                        "eligibilityRule",
                        "profileScorecardGoals"
                      ],
                      "type": "object",
                      "properties": {
                        "eligibilityRule": {
                          "description": "The name of the Compensation Eligibility Rule assigned to the Compensation Scorecard Profile.",
                          "allOf": [
                            {
                              "required": [
                                "id"
                              ],
                              "type": "object",
                              "properties": {
                                "id": {
                                  "pattern": "^(?:(?:[0-9a-f]{32})|(?:[0-9]+\\$[0-9]+)|(\\S+=\\S+))$",
                                  "type": "string",
                                  "description": "wid / id / reference id"
                                },
                                "descriptor": {
                                  "type": "string",
                                  "description": "The display name of the instance",
                                  "readOnly": true
                                },
                                "href": {
                                  "type": "string",
                                  "description": "A link to the instance",
                                  "readOnly": true
                                }
                              }
                            },
                            {
                              "type": "object"
                            }
                          ]
                        },
                        "profileScorecardGoals": {
                          "type": "array",
                          "description": "The \\~goals\\~ for the Compensation Scorecard.",
                          "items": {
                            "allOf": [
                              {
                                "required": [
                                  "goalName",
                                  "goalWeight"
                                ],
                                "type": "object",
                                "properties": {
                                  "goalWeight": {
                                    "type": "integer",
                                    "description": "The weight given to the Compensation Scorecard Goal. [90% will be displayed as 0.9]",
                                    "x-workday-type": "Numeric"
                                  },
                                  "goalName": {
                                    "type": "string",
                                    "description": "The name of the Compensation Scorecard Goal.",
                                    "example": "Lorem ipsum dolor sit amet, cum choro singulis consectetuer ut, ubique iisque contentiones ex duo. Quo lorem etiam eu.",
                                    "x-workday-type": "Text"
                                  },
                                  "goalDescription": {
                                    "type": "string",
                                    "description": "The description of the Compensation Scorecard Goal",
                                    "example": "Lorem ipsum dolor sit amet, cum choro singulis consectetuer ut, ubique iisque contentiones ex duo. Quo lorem etiam eu.",
                                    "x-workday-type": "Text"
                                  },
                                  "id": {
                                    "type": "string",
                                    "description": "Id of the instance"
                                  }
                                }
                              }
                            ]
                          },
                          "x-workday-type": "Multi-instance"
                        },
                        "id": {
                          "type": "string",
                          "description": "Id of the instance"
                        }
                      }
                    }
                  ]
                },
                "x-workday-type": "Multi-instance"
              },
              "scorecardDescription": {
                "type": "string",
                "description": "The description of the Compensation Scorecard.",
                "example": "Lorem ipsum dolor sit amet, cum choro singulis consectetuer ut, ubique iisque contentiones ex duo. Quo lorem etiam eu.",
                "x-workday-type": "Text"
              },
              "effectiveDate": {
                "type": "string",
                "description": "The Effective Date of the Compensation Scorecard in format YYYY-MM-DD",
                "format": "date",
                "x-workday-type": "Date"
              },
              "id": {
                "type": "string",
                "description": "Id of the instance"
              }
            }
          }
        ]
      }
    }
  },
  "required": true
}
