http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "allOf": [
          {
            "type": "object",
            "properties": {
              "gradeAverage": {
                "type": "string",
                "description": "The candidate's grade average at this educational institution.",
                "example": "Lorem ipsum dolor sit amet, cum choro singulis consectetuer ut, ubique iisque contentiones ex duo. Quo lorem etiam eu.",
                "x-workday-type": "Text"
              },
              "degree": {
                "description": "The candidate's degree at this educational institution.",
                "allOf": [
                  {
                    "required": [
                      "id"
                    ],
                    "type": "object",
                    "properties": {
                      "id": {
                        "pattern": "^(?:(?:[0-9a-f]{32})|(?:[0-9]+\\$[0-9]+)|(\\S+=\\S+))$",
                        "type": "string",
                        "description": "wid / id / reference id"
                      },
                      "descriptor": {
                        "type": "string",
                        "description": "The display name of the instance",
                        "readOnly": true
                      },
                      "href": {
                        "type": "string",
                        "description": "A link to the instance",
                        "readOnly": true
                      }
                    }
                  },
                  {
                    "type": "object"
                  }
                ]
              },
              "schoolName": {
                "type": "string",
                "description": "The name of the school the candidate attended or is attending.",
                "example": "Lorem ipsum dolor sit amet, cum choro singulis consectetuer ut, ubique iisque contentiones ex duo. Quo lorem etiam eu.",
                "x-workday-type": "Text"
              },
              "fieldOfStudy": {
                "description": "The field of study for the candidate at this educational institution.",
                "allOf": [
                  {
                    "required": [
                      "id"
                    ],
                    "type": "object",
                    "properties": {
                      "id": {
                        "pattern": "^(?:(?:[0-9a-f]{32})|(?:[0-9]+\\$[0-9]+)|(\\S+=\\S+))$",
                        "type": "string",
                        "description": "wid / id / reference id"
                      },
                      "descriptor": {
                        "type": "string",
                        "description": "The display name of the instance",
                        "readOnly": true
                      },
                      "href": {
                        "type": "string",
                        "description": "A link to the instance",
                        "readOnly": true
                      }
                    }
                  },
                  {
                    "type": "object"
                  }
                ]
              },
              "firstYearAttended": {
                "type": "string",
                "description": "The first year the candidate attended this educational institution.",
                "format": "date",
                "x-workday-type": "Date"
              },
              "lastYearAttended": {
                "type": "string",
                "description": "The last year the candidate attended this educational institution.",
                "format": "date",
                "x-workday-type": "Date"
              },
              "id": {
                "type": "string",
                "description": "Id of the instance"
              }
            }
          }
        ]
      }
    }
  },
  "required": true
}
