http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "allOf": [
          {
            "required": [
              "fileLength"
            ],
            "type": "object",
            "properties": {
              "fileName": {
                "maxLength": 255,
                "type": "string",
                "description": "The file name of the attachment.",
                "example": "Lorem ipsum dolor sit amet, cum choro singulis consectetuer ut, ubique iisque contentiones ex duo. Quo lorem etiam eu.",
                "x-workday-type": "Text",
                "x-workday-confidence-level": "Production"
              },
              "fileLength": {
                "type": "integer",
                "description": "The file length of the attachment.",
                "example": 99999999999,
                "x-workday-type": "Numeric"
              },
              "contentType": {
                "description": "The content type of the attachment.",
                "allOf": [
                  {
                    "required": [
                      "id"
                    ],
                    "type": "object",
                    "properties": {
                      "id": {
                        "pattern": "^(?:(?:[0-9a-f]{32})|(?:[0-9]+\\$[0-9]+)|(\\S+=\\S+))$",
                        "type": "string",
                        "description": "wid / id / reference id"
                      },
                      "descriptor": {
                        "type": "string",
                        "description": "The display name of the instance",
                        "readOnly": true
                      },
                      "href": {
                        "type": "string",
                        "description": "A link to the instance",
                        "readOnly": true
                      }
                    }
                  },
                  {
                    "type": "object"
                  }
                ]
              },
              "descriptor": {
                "type": "string",
                "description": "The display name of the instance",
                "example": "Lorem ipsum dolor sit ame"
              },
              "id": {
                "type": "string",
                "description": "Id of the instance"
              }
            }
          }
        ]
      }
    }
  },
  "required": true
}
