http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "allOf": [
          {
            "required": [
              "evaluationDate",
              "scorecardID"
            ],
            "type": "object",
            "properties": {
              "scorecardName": {
                "type": "string",
                "description": "The \\~goal\\~ name of the scorecard criteria result.",
                "readOnly": true,
                "example": "Lorem ipsum dolor sit amet, cum choro singulis consectetuer ut, ubique iisque contentiones ex duo. Quo lorem etiam eu.",
                "x-workday-type": "Text"
              },
              "evaluationDate": {
                "type": "string",
                "description": "Scorecard Result Evaluation Date",
                "format": "date",
                "x-workday-type": "Date"
              },
              "scorecardID": {
                "description": "Returns the Default Scorecard for the Scorecard Result Instance",
                "allOf": [
                  {
                    "allOf": [
                      {
                        "type": "object",
                        "properties": {
                          "id": {
                            "type": "string",
                            "description": "Id of the instance"
                          }
                        }
                      }
                    ]
                  },
                  {
                    "type": "object"
                  }
                ]
              },
              "descriptor": {
                "type": "string",
                "description": "The display name of the instance",
                "example": "Lorem ipsum dolor sit ame"
              },
              "id": {
                "type": "string",
                "description": "Id of the instance"
              }
            }
          }
        ]
      }
    }
  },
  "required": true
}
